const translateToMorseCode = (input) => {
    const inputArray = [...input];
    const morseCode = inputArray.map(i => {
        const code = language.alphabets[i.toUpperCase()]
            || language.numeral[i] || language.punctuations[i];
        return code || "";
    });
    return morseCode.join(" ");
}

const translateToEnglish = (input) => {
    const inputArray = input.split(" ");
    const engWord = inputArray.map(i => {
        const value = (Object.keys(language.alphabets).filter((key) => { return language.alphabets[key] === i })[0]
            || Object.keys(language.punctuations).filter((key) => { return language.punctuations[key] === i })[0]
            || Object.keys(language.numeral).filter((key) => { return language.numeral[key] === i })[0]);
        return value ? value.toLocaleLowerCase() : "";
    });
    return engWord.join(" ");
}

const playSound = (function beep() {
    const snd = new Audio("data:audio/wav;base64,");
    return () => {
        snd.play();
    }
})();

const playMorseCode = (input) => {
    // . (1)
    // - (2)
    const inputArray = input.split(" ");
    for (let i in inputArray) {
        playSound()
    }
}

const sleep = (time) => {
    return new Promise((resolve) => setTimeout(resolve, time));
}

export { translateToEnglish, translateToMorseCode, playMorseCode, sleep, playSound }